function createTableSchema($tablefields, $file_type) 
{
    $str = "CREATE TABLE epa_data_".$file_type."  ("."\n";
    $end = end($tablefields);
    foreach($tablefields as $field) {

        if($field == $end) {
            $str .= string_to_filename($field) . " varchar(200) NOT NULL DEFAULT ''"."\n";
        }
        else {
            $str .= string_to_filename($field) . " varchar(200) NOT NULL DEFAULT '',"."\n";
        }
    }   

    $str .= ");\n\n";
    return $str;
}

